// Example: Efficient function for data processing
const processUserData = (users) => {
  return users
    .filter(user => user.isActive)
    .map(user => ({
      id: user.id,
      name: user.name,
      email: user.email,
      lastLogin: new Date(user.lastLogin)
    }))
    .sort((a, b) => b.lastLogin - a.lastLogin);
};

